DROP TABLE IF EXISTS `crons`;
CREATE TABLE `crons`  (
  `cron_id` tinyint(3) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '计划任务ID',
  `cron_sn` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '计划任务编号',
  `cron_name` varchar(120) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '名称',
  `cron_desc` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '描述',
  `last_runtime` int(10) NOT NULL DEFAULT 0 COMMENT '上次执行时间',
  `next_runtime` int(10) NOT NULL DEFAULT 0 COMMENT '下次执行时间',
  `cron_type` tinyint(2) NOT NULL DEFAULT 0 COMMENT '定时方式：1：每年，2：每月，3：每周，4：每天，5：每小时，6：每分钟\n11：每隔N年，12：每隔N月，13：每隔N周，14：每隔N天，15：每隔N小时，16：每隔N分钟',
  `cron_config` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '[JSON]存对应cron_type数据',
  `is_enabled` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否开启',
  `just_once` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否仅执行一次',
  `white_ip_list` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '白名单',
  PRIMARY KEY (`cron_id`) USING BTREE,
  INDEX `nextime`(`next_runtime`) USING BTREE,
  INDEX `enable`(`is_enabled`) USING BTREE,
  INDEX `cron_code`(`cron_sn`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 21 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '定时任务表' ROW_FORMAT = Dynamic;

