  `shipping_time` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单配送时间',
  `received_time` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单收货时间',
  `pay_type_id` smallint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '支付类型:1,在线支付（微信、支付等）;2,货到付款（支持先发货，后付款）;3,线下支付',
  `pay_time` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单支付时间',
  `use_points` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '使用的积分的数量',
  `is_need_commisson` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否需要分佣',
  `distribution_status` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否已分销分成',
  `referrer_user_id` mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '推广人userId',
  `is_del` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否删除',
  `shop_id` mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺id',
  `is_store_splited` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否已按店铺拆分:0,否;1,是',
  `comment_status` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否已评价',
  `total_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '订单总金额（商品总价 + 运费等 - 优惠券 - 各种优惠活动）',
  `paid_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '已支付金额(包含使用余额+线上支付金额+线下支付金额)',
  `unpaid_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '未付款金额（计算方式为：total_amount - paid_amount）',
  `unrefund_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '未退款金额（一般出现在订单取消或修改金额、商品后）',
  `product_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '商品的总金额',
  `coupon_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '使用优惠券金额 ',
  `points_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '使用积分金额',
  `discount_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '参加优惠活动金额，如折扣、满减等',
  `balance` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '使用余额金额',
  `online_paid_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '线上支付金额（支付宝、微信等）',
  `offline_paid_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '线下支付金额（银行汇款等）',
  `service_fee` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '服务费',
  `shipping_fee` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '配送费用',
  `invoice_fee` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '发票费用',
  `order_extension` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '【JSON】记录订单使用的优惠券、优惠活动、不同店铺配送等的具体细节信息',
  `order_source` varchar(80) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '下单来源设备，APP|PC|H5|微信公众号|微信小程序',
  `invoice_data` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '【JSON】发票信息',
  `out_trade_no` varchar(120) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '订单支付平台单号',
  `is_settlement` tinyint(2) NULL DEFAULT 0 COMMENT '是否结算0未结算1已结算',
  `is_exchange_order` tinyint(1) NULL DEFAULT 0 COMMENT '是否是积分抵扣订单',
  `order_type` tinyint(2) NULL DEFAULT 1 COMMENT '订单类型：1普通订单，3积分订单，2拼团，5砍一砍 6 虚拟订单 7 付费商品订单 8 卡密订单',
  PRIMARY KEY (`order_id`) USING BTREE,
  UNIQUE INDEX `order_sn`(`order_sn`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  INDEX `order_status`(`order_status`) USING BTREE,
  INDEX `shipping_status`(`shipping_status`) USING BTREE,
  INDEX `pay_status`(`pay_status`) USING BTREE,
  INDEX `shipping_id`(`logistics_id`) USING BTREE,
  INDEX `store_id`(`shop_id`) USING BTREE,
  INDEX `is_del`(`is_del`) USING BTREE,
  INDEX `pay_type_id`(`pay_type_id`) USING BTREE,
  INDEX `add_time`(`add_time`) USING BTREE,
  INDEX `referrer_user_id`(`referrer_user_id`) USING BTREE,
  INDEX `is_need_commisson`(`is_need_commisson`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '订单表' ROW_FORMAT = DYNAMIC;

