DROP TABLE IF EXISTS `product`;
CREATE TABLE `product`  (
  `product_id` mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '商品id ',
  `product_name` varchar(120) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '商品的名称 ',
  `product_sn` varchar(60) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '商品的唯一货号 ',
  `product_tsn` varchar(255) CHARACTER SET utf8 COLLATE utf8_estonian_ci NULL DEFAULT NULL COMMENT '条形编码',
  `product_stock` smallint(5) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品库存数量 ',
  `product_price` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '本店售价 ',
  `market_price` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '市场售价 ',
  `shipping_tpl_id` mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '运费模板id',
  `product_status` tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '该商品状态，1，上架；0，下架',
  `product_type` tinyint(1) NULL DEFAULT 1 COMMENT '商品类型 1普通商品 2虚拟商品3 卡密商品 4 付费内容',
  `category_id` smallint(5) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品所属商品分类id，取值category的cat_id ',
  `brand_id` smallint(5) UNSIGNED NOT NULL DEFAULT 0 COMMENT '品牌id，取值于brand 的brand_id ',
  `shop_id` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺id',
  `keywords` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '商品关键字，放在商品页的关键字中，为搜索引擎收录用 ',
  `shop_category_id` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺分类id',
  `check_status` tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '审核状态:0,待审核;1,审核通过;2,审核未通过',
  `check_reason` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '审核失败原因',
  `click_count` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品点击数',
  `product_weight` decimal(10, 3) UNSIGNED NOT NULL DEFAULT 0.000 COMMENT '商品的重量，以千克为单位 ',
  `is_promote` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否特价促销；0，否；1，是 ',
  `is_promote_activity` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否参加了秒杀',
  `promote_price` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '促销价格 ',
  `promote_start_date` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '促销价格开始日期 ',
  `promote_end_date` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '促销价格结束日期 ',
  `seckill_max_num` smallint(5) UNSIGNED NOT NULL DEFAULT 0 COMMENT '最大秒杀库存',
  `product_brief` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '商品的简短描述 ',
  `product_desc` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '商品的详细描述 ',
  `pic_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '商品的实际大小图片，如进入该商品页时介绍商品属性所显示的大图片',
  `pic_thumb` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '商品在前台显示的微缩图片，如在分类筛选时显示的小图片 ',
  `pic_original` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '应该是上传的商品的原始图片 ',
  `comment_tag` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '标签',
  `free_shipping` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否包邮',
  `integral` int(10) NOT NULL DEFAULT 0 COMMENT '购买该商品可以使用的积分数量，估计应该是用积分代替金额消费；但程序好像还没有实现该功能 ',
  `add_time` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品的添加时间 ',
  `sort_order` smallint(4) UNSIGNED NOT NULL DEFAULT 100 COMMENT '商品的显示顺序',
  `store_sort_order` smallint(4) UNSIGNED NOT NULL DEFAULT 100 COMMENT '店铺的排序',
  `is_delete` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品是否已经删除，0，否；1，已删除 ',
  `is_best` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否是精品:0，否；1，是 ',
  `is_new` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否是新品:0，否；1，是',
  `is_hot` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否热销:0，否；1，是 ',
  `last_update` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '最近一次更新商品配置的时间 ',
  `remark` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '商品的商家备注，仅商家可见 ',
  `give_integral` int(11) NOT NULL DEFAULT -1 COMMENT '购买该商品时每笔成功交易赠送的积分数量 ',
  `rank_integral` int(11) NOT NULL DEFAULT -1 COMMENT '等级积分',
  `suppliers_id` smallint(5) UNSIGNED NULL DEFAULT NULL COMMENT '供应商id',
  `virtual_sales` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '虚拟销量',
  `limit_number` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '限购数量',
  `product_care` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '温馨提示',
  `product_related` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '[JSON]关联的推荐商品Id',
  `product_service_ids` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '[JSON]服务项ids',
  `is_support_return` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否支持无理由退货',
  `is_support_cod` tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '是否支持货到付款:0,否;1,是',
  `product_video` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '商品视频',
  `prepay_price` decimal(8, 2) UNSIGNED NULL DEFAULT 0.00 COMMENT '预付金额',
  `card_group_id` int(13) NOT NULL DEFAULT 0 COMMENT '关联的电子卡券分组',
  `virtual_sample` text COLLATE utf8mb4_unicode_ci COMMENT '虚拟商品',
  `paid_content` text COLLATE utf8mb4_unicode_ci COMMENT '付费内容',
  `no_shipping` tinyint(4) DEFAULT '0' COMMENT '是否无需配送；0-否，1-是',
  PRIMARY KEY (`product_id`, `brand_id`, `product_weight`) USING BTREE,
  INDEX `goods_sn`(`product_sn`) USING BTREE,
  INDEX `cat_id`(`category_id`) USING BTREE,
  INDEX `last_update`(`last_update`) USING BTREE,
  INDEX `brand_id`(`brand_id`) USING BTREE,
  INDEX `goods_weight`(`product_weight`) USING BTREE,
  INDEX `promote_end_date`(`promote_end_date`) USING BTREE,
  INDEX `promote_start_date`(`promote_start_date`) USING BTREE,
  INDEX `goods_number`(`product_stock`) USING BTREE,
  INDEX `sort_order`(`sort_order`) USING BTREE,
  INDEX `goods_basesell`(`virtual_sales`) USING BTREE,
  INDEX `store_id`(`shop_id`) USING BTREE,
  INDEX `product_name`(`product_name`) USING BTREE,
  INDEX `keywords`(`keywords`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1000 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '商品表' ROW_FORMAT = DYNAMIC;

