DROP TABLE IF EXISTS `access_log`;
CREATE TABLE `access_log`
(
    `id`            int(11) UNSIGNED NOT NULL AUTO_INCREMENT,
    `access_time`   int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '访问时间',
    `ip_address`    varchar(15) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT 'ip地址',
    `product_id`    mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品id',
    `shop_id`       mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺id',
    `user_id`       mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '用户id',
    `params_id`     mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '参数id',
    `controller`    varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '控制器',
    `access_domain` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '访问域名',
    `access_path`   varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '访问地址',
    PRIMARY KEY (`id`) USING BTREE,
    INDEX           `access_time`(`access_time`) USING BTREE,
    INDEX           `shop_id`(`shop_id`) USING BTREE,
    INDEX           `goods_id`(`product_id`) USING BTREE,
    INDEX           `ip_address`(`ip_address`) USING BTREE,
    INDEX           `user_id`(`user_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '访问日志表' ROW_FORMAT = Dynamic;

