DROP TABLE IF EXISTS `admin_msg`;
CREATE TABLE `admin_msg`
(
    `msg_id`       smallint(5) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '自增id',
    `msg_type`     tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '消息类型,11:新订单,12:已付款订单,13:订单完成,21:商品库存预警,22:商品无货,23:商品下架;31:有效订单被取消,32:售后申请,41:店铺入驻申请,42:店铺资质修改,43:店铺违规',
    `send_time`    int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '消息发送时间',
    `is_readed`    tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '消息是否阅读,1:已阅读;0:未阅读',
    `shop_id`      int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '消息是否发向指定店铺',
    `admin_id`     int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '消息是否发向指定管理员',
    `order_id`     int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单消息',
    `product_id`   int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单商品消息',
    `title`        varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '消息主题',
    `content`      text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '消息内容',
    `msg_link`     varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '消息链接',
    `related_data` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '[json]跳转数据信息',
    PRIMARY KEY (`msg_id`) USING BTREE,
    INDEX          `msg_type`(`msg_type`) USING BTREE,
    INDEX          `is_readed`(`is_readed`) USING BTREE,
    INDEX          `shop_id`(`shop_id`) USING BTREE,
    INDEX          `admin_id`(`admin_id`) USING BTREE,
    INDEX          `order_id`(`order_id`) USING BTREE,
    INDEX          `goods_id`(`product_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '后台消息' ROW_FORMAT = Dynamic;

