    INDEX            `parent_id`(`parent_id`) USING BTREE,
    INDEX            `is_show`(`is_show`) USING BTREE,
    INDEX            `sort_order`(`sort_order`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 10000397 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '商城后台权限表' ROW_FORMAT = Dynamic;

