-- ----------------------------
-- Table structure for bargain
-- ----------------------------
DROP TABLE IF EXISTS `bargain`;
CREATE TABLE `bargain`
(
    `bargain_id`      mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT,
    `bargain_name`    varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '砍一砍活动名称',
    `bargain_pic`     varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '砍一砍活动图片',
    `start_time`      int(11) UNSIGNED NOT NULL COMMENT '砍一砍活动开始时间',
    `end_time`        int(11) UNSIGNED NOT NULL COMMENT '砍一砍活动结束时间',
    `cut_price_limit` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '砍到多少钱',
    `cut_num_limit`   int(11) NOT NULL DEFAULT 0 COMMENT '邀请多少人砍',
    `first_cut_range` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '首次砍价范围',
    `cut_range`       varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '砍价范围',
    `product_id`      mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品id',
    `sku_id`          mediumint(8) NOT NULL DEFAULT 0 COMMENT '商品sku_id',
    `is_show`         tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否展示',
    `sort_order`      smallint(4) UNSIGNED NOT NULL DEFAULT 50 COMMENT '排序',
    `success_type`    tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '状态：1:进行中，2:成功',
    `shop_id`         mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '所属店铺',
    PRIMARY KEY (`bargain_id`) USING BTREE,
    INDEX             `is_show`(`is_show`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '砍一砍' ROW_FORMAT = DYNAMIC;

