    INDEX            `product_id`(`product_id`) USING BTREE,
    INDEX            `sku_id`(`sku_id`) USING BTREE,
    INDEX            `store_id`(`shop_id`) USING BTREE,
    INDEX            `is_checked`(`is_checked`) USING BTREE,
    INDEX            `type`(`type`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '购物车购物信息记录表' ROW_FORMAT = Dynamic;

