-- ----------------------------
-- Table structure for comment
-- ----------------------------
DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment`
(
    `comment_id`    int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '用户评论的自增id ',
    `user_id`       int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '发表该评论的用户的用户id,取值user的user_id ',
    `username`      varchar(60) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  NOT NULL DEFAULT '' COMMENT '评论该文章或商品的人的名称,取值users的user_name ',
    `avatar`        varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '会员头像',
    `product_id`    mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品id',
    `order_id`      int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单id',
    `order_item_id` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单商品itemId',
    `comment_rank`  tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '星级，5代表5星 ',
    `content`       text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '评论的内容',
    `add_time`      int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '评论的时间',
    `status`        tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '状态，0：待审核，1：审核通过，2：无效',
    `parent_id`     int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否为回复',
    `usefull`       int(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '评价有用',
    `useless`       int(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '评价无用',
    `comment_tag`   text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '【JSON】标签[string]',
    `show_pics`     text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '【JSON】晒单图片[{pic_thumb,pic_url}]',
    `is_recommend`  tinyint(3) UNSIGNED NULL DEFAULT 0 COMMENT '是否推荐',
    `is_top`        tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否置顶',
    `is_showed`     tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否已晒单',
    `is_default`    tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '系统默认评价',
    `sort_order`    mediumint(8) UNSIGNED NOT NULL DEFAULT 50 COMMENT '排序',
    `shop_id`       mediumint(8) NOT NULL DEFAULT 0 COMMENT '所属店铺',
    PRIMARY KEY (`comment_id`) USING BTREE,
    INDEX           `parent_id`(`parent_id`) USING BTREE,
    INDEX           `product_id`(`product_id`) USING BTREE,
    INDEX           `order_item_id`(`order_item_id`) USING BTREE,
    INDEX           `is_default`(`is_default`) USING BTREE,
    INDEX           `sort_order`(`sort_order`) USING BTREE,
    INDEX           `user_id`(`user_id`) USING BTREE,
    INDEX           `is_showed`(`is_showed`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '用户对文章和产品的评论列表' ROW_FORMAT = Dynamic;

