-- ----------------------------
-- Table structure for coupon
-- ----------------------------
DROP TABLE IF EXISTS `coupon`;
CREATE TABLE `coupon`
(
    `coupon_id`         smallint(5) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '优惠券id',
    `coupon_name`       varchar(60) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  NOT NULL DEFAULT '' COMMENT '优惠券名称',
    `coupon_money`      decimal(10, 2)                                          NOT NULL DEFAULT 0.00 COMMENT '优惠券金额',
    `coupon_discount`   decimal(4, 1) UNSIGNED NOT NULL DEFAULT 10.0 COMMENT '优惠券折扣',
    `coupon_desc`       varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '优惠券描述',
    `coupon_type`       tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '优惠券类型，1：满减券，2：折扣券',
    `send_range`        tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '使用范围:0,全部商品;3,指定商品;4,不包含指定商品',
    `send_range_data`   text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '[json]使用范围相关信息',
    `min_order_amount`  decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '最少订单金额',
    `send_start_date`   int(11) NOT NULL DEFAULT 0 COMMENT '优惠券可领取的开始时间 (已废弃)',
    `send_end_date`     int(11) NOT NULL DEFAULT 0 COMMENT '优惠券可领取的结束时间 (已废弃)',
    `send_type`         tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '优惠券使用时间方式，0：按领取后多少天内可用，1：按固定时间范围可用',
    `use_day`           smallint(3) NOT NULL DEFAULT 30 COMMENT '优惠券多少天过期',
    `use_start_date`    int(11) NOT NULL DEFAULT 0 COMMENT '优惠券可以使用的开始时间',
    `use_end_date`      int(11) NOT NULL DEFAULT 0 COMMENT '优惠券可以使用的结束时间',
    `is_show`           tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否展示',
    `is_global`         tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '全场通用券（跨店铺）:0,否;1,是',
    `is_new_user`       tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否为新人专享优惠券',
    `enabled_click_get` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商城点击领取:0,否;1,是',
    `limit_user_rank`   text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '[JSON]仅限会员等级',
    `shop_id`           mediumint(8) UNSIGNED NULL DEFAULT 0 COMMENT '店铺id',
    `is_delete`         tinyint(1) NULL DEFAULT 0 COMMENT '是否删除',
    `limit_num`         int(11) NULL DEFAULT 0 COMMENT '限制领取数量',
    `delay_day`         int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '延迟天数：0 为立即生效 ',
    `send_num`          int(11) UNSIGNED NOT NULL DEFAULT 1 COMMENT '发放总量',
    `max_order_amount`  decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '优惠金额上限',
    `coupon_unit`       tinyint(3) UNSIGNED NOT NULL DEFAULT 1 COMMENT '优惠单位：1 元  2 件',
    `reduce_type`       tinyint(3) UNSIGNED NOT NULL DEFAULT 1 COMMENT '满减类型：1 满多少 2 无门槛',
    `add_time`          int(11) NULL DEFAULT 0 COMMENT '添加时间',
    PRIMARY KEY (`coupon_id`) USING BTREE,
    INDEX               `is_show`(`is_show`) USING BTREE,
    INDEX               `use_start_date`(`use_start_date`) USING BTREE,
    INDEX               `use_end_date`(`use_end_date`) USING BTREE,
    INDEX               `send_start_date`(`send_start_date`) USING BTREE,
    INDEX               `send_end_date`(`send_end_date`) USING BTREE,
    INDEX               `add_time`(`add_time`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = ' 红包类型表' ROW_FORMAT = Dynamic;

