DROP TABLE IF EXISTS `crons`;
CREATE TABLE `crons`
(
    `cron_id`       tinyint(3) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '计划任务ID',
    `cron_sn`       varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  NOT NULL DEFAULT '' COMMENT '计划任务编号',
    `cron_name`     varchar(120) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '名称',
    `cron_desc`     text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '描述',
