    PRIMARY KEY (`groupon_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '商品拼团表' ROW_FORMAT = DYNAMIC;

