DROP TABLE IF EXISTS `im_conversation`;
CREATE TABLE `im_conversation`
(
    `id`               int(11) NOT NULL AUTO_INCREMENT,
    `user_id`          int(11) NULL DEFAULT NULL,
    `last_servant_id`  int(11) NULL DEFAULT 0,
    `add_time`         int(11) NULL DEFAULT NULL,
    `shop_id`          int(11) NULL DEFAULT NULL,
    `user_from`        varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
    `status`           tinyint(2) NULL DEFAULT 0 COMMENT '会话状态0待接入1会话中2已结束',
    `last_update_time` int(11) NULL DEFAULT NULL,
    `is_delete`        tinyint(2) NULL DEFAULT 0 COMMENT '0未删除1已删除',
    `remark`           varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '会话备注',
    `summary`          varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '会话总结',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

