-- ----------------------------
-- Table structure for im_message
-- ----------------------------
DROP TABLE IF EXISTS `im_message`;
CREATE TABLE `im_message`
(
    `id`              int(11) NOT NULL AUTO_INCREMENT,
    `conversation_id` int(11) NULL DEFAULT NULL COMMENT '来自会话',
    `content`         text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '内容',
    `message_type`    varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT 'text文本 image图片 custom自定义 sound语音 video视频 file文件',
    `type`            tinyint(2) NULL DEFAULT NULL COMMENT '类型1用户发的，2客服发的，3系统',
    `user_id`         int(11) NULL DEFAULT NULL COMMENT '用户id',
    `servant_id`      int(11) NULL DEFAULT NULL COMMENT '客服id',
    `send_time`       int(11) NULL DEFAULT NULL COMMENT '发送时间',
    `status`          tinyint(2) NULL DEFAULT 1 COMMENT '状态，1正常，2删除，3撤回',
    `extend`          text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '消息扩展信息',
    `push_status`     tinyint(2) NULL DEFAULT 0 COMMENT '推送状态0未推送，1已推送',
    `is_read`         tinyint(1) NULL DEFAULT 0 COMMENT '是否已读0未读，1已读',
    `shop_id`         int(11) NOT NULL DEFAULT 0 COMMENT '店铺id',
    `user_from`       varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '来源',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '会话消息表' ROW_FORMAT = Dynamic;

