    `merchant_data`     text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '商户数据json',
    `status`            tinyint(2) NULL DEFAULT 1 COMMENT '状态1正常2取消资格',
    `type`              tinyint(1) NULL DEFAULT 1 COMMENT '类型1个人2企业',
