    INDEX                    `is_hot`(`is_hot`) USING BTREE,
    INDEX                    `is_enabled`(`is_enabled`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '积分兑换表' ROW_FORMAT = Dynamic;

