-- ----------------------------
-- Table structure for product_package
-- ----------------------------
DROP TABLE IF EXISTS `product_package`;
CREATE TABLE `product_package`
(
    `package_id`   mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '组合套餐id',
    `package_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '促销活动的名称 ',
    `package_desc` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '促销活动的描述',
    `package_type` tinyint(3) UNSIGNED NOT NULL COMMENT '4,组合优惠',
    `product_id`   mediumint(8) UNSIGNED NOT NULL COMMENT '参加活动的id，取值于goods的goods_id ',
    `start_time`   int(10) UNSIGNED NOT NULL COMMENT '活动开始时间 ',
    `end_time`     int(10) UNSIGNED NOT NULL COMMENT '活动结束时间 ',
    `is_finished`  tinyint(3) UNSIGNED NOT NULL COMMENT '活动是否结束，0，结束；1，未结束',
    `ext_info`     text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
    `store_id`     mediumint(8) UNSIGNED NOT NULL DEFAULT 0,
    PRIMARY KEY (`package_id`) USING BTREE,
    INDEX          `act_name`(`package_name`, `package_type`, `product_id`) USING BTREE,
    INDEX          `store_id`(`store_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '拍卖活动和夺宝奇兵活动配置信息' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of product_package
-- ----------------------------

-- ----------------------------
-- Table structure for product_promotion
-- ----------------------------
DROP TABLE IF EXISTS `product_promotion`;
CREATE TABLE `product_promotion`
(
    `promotion_id`        smallint(5) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '活动id',
    `promotion_name`      varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '活动名称',
    `start_time`          int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '开始时间',
    `end_time`            int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '结束时间',
    `limit_user_rank`     varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '[JSON]仅限会员等级',
    `range`               tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '活动范围:0,全部商品;3,指定商品.4，不含以下商品',
    `range_data`          text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '[json]活动范围相关信息',
    `min_order_amount`    decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '订单金额下限',
    `max_order_amount`    decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '订单金额上限，可用于阶梯设置',
    `promotion_type`      tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '优惠类型，1：满减，2：折扣，3：满赠',
    `promotion_type_data` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '[data]类型相关数据，如满减金额、折扣金额、赠品等',
    `is_available`        tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '是否启用',
    `sort_order`          tinyint(3) UNSIGNED NOT NULL DEFAULT 50 COMMENT '排序',
    `shop_id`             int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺ID',
    `rules_type`          tinyint(3) UNSIGNED NOT NULL DEFAULT 1 COMMENT '优惠规则：1 阶梯优惠 0 循环优惠',
    `unit`                tinyint(3) UNSIGNED NOT NULL DEFAULT 1 COMMENT '优惠单位：1 元 2 件',
    PRIMARY KEY (`promotion_id`) USING BTREE,
    INDEX                 `act_name`(`promotion_name`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '商品促销' ROW_FORMAT = Dynamic;

