DROP TABLE IF EXISTS `refund_apply`;
CREATE TABLE `refund_apply`
(
    `refund_id`        mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '退款申请id',
    `refund_type`      tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '1:订单,2:商品',
    `order_id`         int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单id',
    `user_id`          int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '会员id',
    `aftersale_id`     int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '售后id',
    `refund_status`    tinyint(2) UNSIGNED NOT NULL DEFAULT 0 COMMENT '0:未处理,1:处理中，2已处理,3:取消',
    `add_time`         int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '添加时间',
    `refund_note`      varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '回复内容',
    `online_balance`   decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '线上退款金额',
    `offline_balance`  decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '线下退款金额',
    `refund_balance`   decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '退款余额',
    `is_online`        tinyint(2) UNSIGNED NOT NULL DEFAULT 0 COMMENT '线上金额是否到账:0 无需处理1处理中 2已处理',
    `is_offline`       tinyint(2) UNSIGNED NOT NULL DEFAULT 0 COMMENT '线下金额是否到账:0 无需处理1处理中 2已处理',
    `is_receive`       tinyint(2) UNSIGNED NOT NULL DEFAULT 0 COMMENT '余额是否到账:0 无需处理1处理中 2已处理',
    `paylog_refund_id` int(11) NOT NULL DEFAULT 0 COMMENT '线上退款的关联id',
    `shop_id`          mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺id',
    `payment_voucher` text CHARACTER SET utf8 COMMENT '打款凭证',
    PRIMARY KEY (`refund_id`) USING BTREE,
    INDEX              `refund_type`(`refund_type`) USING BTREE,
    INDEX              `order_id`(`order_id`) USING BTREE,
    INDEX              `rec_id`(`aftersale_id`) USING BTREE,
    INDEX              `refund_status`(`refund_status`) USING BTREE,
    INDEX              `aftersale_id`(`aftersale_id`) USING BTREE,
    INDEX              `user_id`(`user_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '退款申请' ROW_FORMAT = Dynamic;

