DROP TABLE IF EXISTS `refund_log`;
CREATE TABLE `refund_log`
(
    `log_id`          mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '接口退款日志id',
    `refund_apply_id` mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '来自的退款申请id',
    `refund_type`     tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '退款类型：1：接口原路退回，2：余额退回，3：线下退回',
    `refund_pay_code` varchar(80) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '接口退款时的支付方式code，比如wechat、alipay等',
    `transaction_id`  varchar(80) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '接口退款时的支付流水号',
    `refund_amount`   decimal(8, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '实际退款金额',
    `add_time`        int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '退款时间',
    `description`     text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '退款描述',
    `user_id`         mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '退款会员id',
    `order_id`        mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '退款的订单ID',
    PRIMARY KEY (`log_id`) USING BTREE,
    INDEX             `refund_id`(`refund_apply_id`) USING BTREE,
    INDEX             `user_id`(`user_id`) USING BTREE,
    INDEX             `order_id`(`order_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '退款申请日志' ROW_FORMAT = Dynamic;

