DROP TABLE IF EXISTS `shipping_tpl`;
CREATE TABLE `shipping_tpl`
(
    `shipping_tpl_id`   mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'id',
    `shipping_time`     varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '配送时间',
    `shipping_tpl_name` varchar(120) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '模板名称',
    `is_free`           tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否包邮',
    `pricing_type`      int(8) UNSIGNED NOT NULL DEFAULT 1 COMMENT '1：件数，2：重量',
    `is_default`        tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否默认',
    `shop_id`           mediumint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺id',
    INDEX               `shipping_tpl_id`(`shipping_tpl_id`) USING BTREE,
    INDEX               `store_id`(`shop_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 80 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '运费模板' ROW_FORMAT = Dynamic;

