DROP TABLE IF EXISTS `shipping_tpl_info`;
CREATE TABLE `shipping_tpl_info`
(
    `id`               int(11) UNSIGNED NOT NULL AUTO_INCREMENT,
    `shipping_type_id` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '配送类型ID',
    `shipping_tpl_id`  int(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '运费模板ID',
    `is_free`          tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否包邮',
    `is_default`       tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否默认',
    `region_data`      text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '[JSON]地区信息，含id和name',
    `start_number`     decimal(10, 1) NOT NULL DEFAULT 0.0 COMMENT '起重或起件',
    `start_price`      decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '起价',
    `add_number`       decimal(10, 1) NOT NULL DEFAULT 0.0 COMMENT '每增重或增价',
    `add_price`        decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '增价',
    `free_price`       decimal(8, 2)  NOT NULL DEFAULT 0.00 COMMENT '满多少包邮件',
    `pricing_type`     tinyint(1) NOT NULL DEFAULT 1 COMMENT '按重还是按件，1:按件，2：按重',
    PRIMARY KEY (`id`) USING BTREE,
    INDEX              `shipping_tpl_id`(`shipping_tpl_id`) USING BTREE,
    INDEX              `is_free`(`is_free`) USING BTREE,
    INDEX              `shipping_type_id`(`shipping_type_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 190 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '运费模板内容' ROW_FORMAT = Dynamic;

