DROP TABLE IF EXISTS `shop`;
CREATE TABLE `shop`
(
    `shop_id`         int(11) NOT NULL AUTO_INCREMENT,
    `shop_title`      varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '店铺名称',
    `add_time`        int(11) NULL DEFAULT 0 COMMENT '店铺创建时间',
    `shop_logo`       varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '店铺logo',
    `click_count`     int(11) NULL DEFAULT 0 COMMENT '点击量',
    `status`          tinyint(2) NULL DEFAULT 1 COMMENT '店铺状态1开业  4暂停运营 10关店  ',
    `merchant_id`     int(11) UNSIGNED NULL DEFAULT 0 COMMENT '商户id',
    `shop_money`      decimal(10, 2) NULL DEFAULT 0.00 COMMENT '店铺资金',
    `frozen_money`    decimal(10, 2) NULL DEFAULT 0.00 COMMENT '冻结资金',
    `contact_mobile`  varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '联系电话',
    `description`     varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '简介',
    `kefu_phone`      varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '客服电话',
    `kefu_weixin`     varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '客服微信',
    `kefu_link`       varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '客服链接',
    `is_contact_kefu` tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '是否联系客服：1 是 0 否',
    `kefu_inlet`      text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '[json]客服入口信息页面： 1 商品详情页 2 订单页',
    PRIMARY KEY (`shop_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '店铺表' ROW_FORMAT = Dynamic;

