                              PRIMARY KEY (`statement_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 2 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '对账单明细表' ROW_FORMAT = DYNAMIC;

